var startCount = 30; 
var started = false; 

var count = 0;
var baseSpeed = 18;

var laneChangeCount = 0;

var PhysicsUpdate = function(api) {
    
    startCount--;
    
    laneChangeCount++;
    
    var distance = api.GetDistanceToNextCorner();
    
    if ((distance > 250 && distance < 300) || (!started && startCount < 0)) {
        api.Boost();
        started = true;
    }
    
    count--;
    
    if (api.CarInFront() || api.CarOnRight() || api.CarOnLeft()) {
        baseSpeed += 0.02;
        count = 100;
    } else {
        baseSpeed -= 0.1;
        baseSpeed = Math.max(18, baseSpeed);
    }
    
    if (laneChangeCount > 100) {
        if (api.GetNextCornerAmount() < 0 || api.CarOnRight()) {
            api.ChangeLaneLeft();
        } else if (api.GetNextCornerAmount() > 0 || api.CarOnLeft()) {
            api.ChangeLaneRight();
        }
        
        laneChangeCount = 0;
    }
    
    var maxSpeed = ((distance * distance) / 900) + baseSpeed;

    if (api.GetSpeed() < maxSpeed) {
        api.SetThrottle(70);
    }
    else {
        if (distance  < 70) {
           api.SetThrottle(-50);
        } else {
            api.SetThrottle(0);
        }
    }
};
